var colors = require('colors');

function insertBefore(msg, beforemsg) {
    msg = typeof msg === 'object' ? JSON.stringify(msg) : msg;
    return beforemsg + msg;
}

module.exports = {

    info: function(msg) {
        msg = insertBefore(msg, "[INFO]:");
        return console.log(msg.green);
    },

    error: function(msg) {
        msg = insertBefore(msg, "[ERROR]:");
        return console.log(msg.red);
    },

    warn: function(msg) {
        msg = insertBefore(msg, "[WARN]:");
        return console.log(msg.yellow);
    },

    debug: function(msg) {
        msg = insertBefore(msg, "[DEBUG]:");
        return console.log(msg.grey);
    },

    trace: function(msg) {
        msg = insertBefore(msg, "[TRACE]:");
        return console.log(msg.cyan);
    }

};