var colors = require('colors');

// log级别对应颜色配置
var colorConfig = {
    info: 'green',
    debug: 'grey',
    trace: 'cyan',
    warn: 'yellow',
    error: 'red'
}

function logBatch(logs, type) {
    var _log, prefix, color;
    for (var i in logs) {
        prefix = '[' + type.toUpperCase() + ']:';
        _log   = prefix + logs[i];
        color  = colorConfig[type];
        console.log(_log[color]);
    }
}

module.exports = {

    info: function () {
        return logBatch(_arguments, 'info');
    },

    error: function () {
        return logBatch(arguments, 'error');
    },

    warn: function () {
        return logBatch(arguments, 'yellow');
    },

    debug: function () {
        return logBatch(arguments, 'debug');
    },

    trace: function (msg) {
        return logBatch(arguments, 'trace');
    }

};