var defaultConfig = {
    md5: true,
    md5Length: 7,
    delimiter: '_'
};

function _extend(source, target) {
    if(typeof source === 'object' && typeof target === 'object'){
        for(var key in target){
            if(target.hasOwnProperty(key)){
                source[key] = _extend(source[key], target[key]);
            }
        }
    } else {
        source = target;
    }

    return source;
}

function Config(){
    this.data = defaultConfig;

    this.set = function(k ,v) {
        this.data[k] = v;
    };

    this.merge = function(target) {
        return _extend(this.data, target);
    }
}

module.exports = new Config();
